import styled from "styled-components";
import CheckBox from "./CheckBox";
import { OneTodoItem } from "../model/Todo";
import Text from "./Text";
import { RiDeleteBack2Fill } from "react-icons/ri";

interface OneTodoProps {
  todo: OneTodoItem;
  onCheckBox: (id: number) => void;
}

const OneTodo = ({ todo, onCheckBox }: OneTodoProps) => {
  const { text, checked, id } = todo;

  return (
    <S.Wrapper>
      <CheckBox checked={checked} onCheckBox={onCheckBox} id={id} />
      <Text children={text} checked={checked} />
      <div style={{ position: "absolute", right: 30 }}>
        <RiDeleteBack2Fill color="red" />
      </div>
    </S.Wrapper>
  );
};
export default OneTodo;

const Wrapper = styled.div`
  display: flex;
  width: 100%;
  height: 30px;
  padding-top: 10px;
  border-bottom: 2px solid #0e9aa4;
  position: relative;
`;

const S = { Wrapper };
