import styled from "styled-components";
import { OneTodoItem } from "../model/Todo";
import React, { useState } from "react";

interface NewInputProps {
  setTodos: React.Dispatch<React.SetStateAction<OneTodoItem[]>>;
  todo: OneTodoItem;
}

const NewInput = ({ setTodos, todo }: NewInputProps) => {
  const [text, setText] = useState("");

  const onChangeInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    setText(e.target.value);
  };
  const newTodo: OneTodoItem = {
    text: text,
    checked: false,
    id: Math.random() * 1000,
  };

  const onSubmitTodo = () => {
    setTodos({ ...todo, newTodo });
  };

  return (
    <Wrapper onClick={onSubmitTodo}>
      <Input placeholder="할일 입력..." onChange={onChangeInput} />
      <Button>추가</Button>
    </Wrapper>
  );
};
export default NewInput;

const Input = styled.input`
  width: 450px;
  height: 30px;
  background-color: #dbdbdb;
  border: none;
`;

const Button = styled.button`
  width: 50px;
  height: 30px;
`;

const Wrapper = styled.form`
  display: flex;
  width: 500px;
`;
