import styled from "styled-components";
import CheckBox from "./CheckBox";
import { OneTodoItem } from "../model/Todo";
import Text from "./Text";
import { RiDeleteBack2Fill } from "react-icons/ri";
import { BiSolidPencil } from "react-icons/bi";
import { useState } from "react";

interface OneTodoProps {
  todo: OneTodoItem;
  onCheckBox: (id: number) => void;
  onDeleteTodo: (id: number) => void;
}

const OneTodo = ({ todo, onCheckBox, onDeleteTodo }: OneTodoProps) => {
  const { text, checked, id } = todo;
  const [isEditMode, setIsEditMode] = useState(false);

  const onClickEditMode = () => {
    setIsEditMode((prev) => !prev);
  };

  return (
    <S.Wrapper>
      <CheckBox checked={checked} onCheckBox={onCheckBox} id={id} />
      <Text isEditMode={isEditMode} children={text} checked={checked} />

      <div style={{ position: "absolute", right: 0 }}>
        {/*수정*/}
        <div style={{ position: "absolute", right: 30 }}>
          <BiSolidPencil size={20} color="blue" />
        </div>
        {/*삭제*/}
        <RiDeleteBack2Fill
          color="red"
          size={20}
          onClick={() => {
            onDeleteTodo(id);
          }}
        />
      </div>
    </S.Wrapper>
  );
};
export default OneTodo;

const Wrapper = styled.div`
  display: flex;
  width: 100%;
  height: 30px;
  padding-top: 10px;
  border-bottom: 2px solid #0e9aa4;
  position: relative;
`;

const S = { Wrapper };
